use catalog service_center;
use schema final;

SELECT
  Branch_ID,
  overall_score
FROM 
  service_center.final.gold_branch_convenience_score;

use catalog service_center;
use schema final;

SELECT
  Branch_ID,
  Model,
  Branch_Convenience_Score,
  overall_score
FROM 
  service_center.final.gold_success_score;

USE CATALOG service_center;
USE SCHEMA final;

SELECT ds, yhat
FROM service_center.final.forecast_monthly_customers_sc
WHERE ds > LAST_DAY(CURRENT_DATE - INTERVAL '1' MONTH)
ORDER BY ds;

USE CATALOG service_center;
USE SCHEMA final;

SELECT 
    DATE(invoice_month) AS invoice_date,
    monthly_customers
FROM service_center.final.forecast_monthly_customers_sc_past
